mapper.select:
SELECT

FROM dc_act_assignee WHERE 1 = 1

    AND `KEY` = #{key,jdbcType=VARCHAR}


    AND SID = #{sid,jdbcType=VARCHAR}


    AND ASSIGNEE = #{assignee,jdbcType=VARCHAR}


    AND GROUP_ID = #{groupId,jdbcType=VARCHAR}


    AND ROLE_ID = #{roleId,jdbcType=VARCHAR}


    AND ASSIGNEE_TYPE = #{assigneeType,jdbcType=VARCHAR}


    AND ACTIVTI_NAME = #{activtiName,jdbcType=VARCHAR}
