mapper.select:
SELECT

FROM dc_act_hi_actinst WHERE 1 = 1

  AND `PROC_DEF_ID` = #{procDefId,jdbcType=VARCHAR}


  AND PROC_INST_ID = #{procInstId,jdbcType=VARCHAR}


  AND EXECUTION_ID = #{executionId,jdbcType=VARCHAR}


  AND ACT_ID = #{ACT_ID,jdbcType=VARCHAR}


  AND TASK_ID = #{taskId,jdbcType=VARCHAR}


  AND CALL_PROC_INST_ID = #{callProcInstId,jdbcType=VARCHAR}


  AND ACT_NAME = #{actName,jdbcType=VARCHAR}

ORDER BY
START_TIME ASC
