mapper.select:
select

FROM dc_act_re_procdef WHERE 1 = 1

    AND REV = #{rev}


    AND CATEGORY = #{category}


    AND `NAME` = #{name}


    AND `KEY` = #{key}


    AND VERSION = #{version}


    AND DEPLOYMENT_ID = #{deploymentId}


    AND RESOURCE_NAME = #{resourceName}


    AND DGRM_RESOURCE_NAME = #{dgrmResourceName}


    AND DESCRIPTION = #{description}


    AND HAS_START_FORM_KEY = #{hasStartFormKey}


    AND HAS_GRAPHICAL_NOTATION = #{hasGraphicalNotation}


    AND SUSPENSION_STATE = #{suspensionState}


    AND TENANT_ID = #{tenantId}


    AND ENGINE_VERSION =#{engineVersion}


    AND RESOURCE_TIME = #{resourceTime}

ORDER BY `KEY`, VERSION DESC
