mapper.select:
SELECT

FROM dc_act_ru_task WHERE 1 = 1

    AND REV = #{rev,jdbcType=INTEGER}


    AND EXECUTION_ID = #{executionId,jdbcType=VARCHAR}


    AND PROC_INST_ID = #{procInstId,jdbcType=VARCHAR}


    AND PROC_DEF_ID = #{procDefId,jdbcType=VARCHAR}


    AND `NAME` = #{name,jdbcType=VARCHAR}


    AND PARENT_TASK_ID = #{parentTaskId,jdbcType=VARCHAR}


    AND DESCRIPTION = #{description,jdbcType=VARCHAR}


    AND TASK_DEF_KEY = #{taskDefKey,jdbcType=VARCHAR}


    AND OWNER = #{owner,jdbcType=VARCHAR}


    AND ASSIGNEE = #{assignee,jdbcType=VARCHAR}


    AND DELEGATION = #{delegation,jdbcType=VARCHAR}


    AND PRIORITY = #{priority,jdbcType=INTEGER}


    AND CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}


    AND DUE_DATE = #{dueDate,jdbcType=TIMESTAMP}


    AND CATEGORY = #{category,jdbcType=VARCHAR}


    AND SUSPENSION_STATE = #{suspensionState,jdbcType=INTEGER}


    AND TENANT_ID = #{tenantId,jdbcType=VARCHAR}


    AND FORM_KEY = #{formKey,jdbcType=VARCHAR}


    AND CLAIM_TIME = #{claimTime,jdbcType=TIMESTAMP}


    AND `ID` = #{id,jdbcType=TIMESTAMP}
